<?php	class ModelPropertyEnquiry extends Model{	public function addEnquiry($data){		$sql="INSERT INTO " . DB_PREFIX . "property_enquiry  set description='".$this->db->escape($data['description'])."',name='".$this->db->escape($data['name'])."',		email='".$this->db->escape($data['email'])."',property_id='".(int) $data['property_id']."',property_agent_id='".(int) $data['property_agent_id']."',date_added=now()";		$this->db->query($sql);	}	public function getEnquirys($data){		$sql="select * from " . DB_PREFIX . "property_enquiry where enquiry_id<>0";		$sort_data = array(		'name',		'email',		);		if (isset($data['sort']) && in_array($data['sort'], $sort_data)){			$sql .= " ORDER BY " . $data['sort'];		}else{		$sql .= " ORDER BY name";		}		if (isset($data['order']) && ($data['order'] == 'DESC')) {			$sql .= " DESC";		}else {		$sql .= " ASC";		}		if (isset($data['start']) || isset($data['limit'])) {			if ($data['start'] < 0) {			$data['start'] = 0;		}		if ($data['limit'] < 1) {			$data['limit'] = 20;		}			$sql .= " LIMIT " . (int)$data['start'] . "," . (int)$data['limit'];		}		$query = $this->db->query($sql);		return $query->rows;	}	public function getEnquiry($enquiry_id){		$sql="select * from " . DB_PREFIX . "property_enquiry where enquiry_id='".$enquiry_id."'";		$query=$this->db->query($sql);		return $query->row;	}	public function editEnquiry($enquiry_id,$data){		$sql="update " . DB_PREFIX . "property_enquiry set description='".$data['description']."',name='".$data['name']."',email='".$data['email']."',property_id='".$data['property_id']."',property_agent_id='".$data['property_agent_id']."',date_modified=now() where enquiry_id='".$enquiry_id."'";		$this->db->query($sql);	}	public function deleteEnquiry($enquiry_id){		$sql="delete  from " . DB_PREFIX . "property_enquiry where enquiry_id='".$enquiry_id."'";		$query=$this->db->query($sql);			}	public function getTotalEnquiry($data) {		$query = $this->db->query("SELECT COUNT(*) AS total FROM " . DB_PREFIX . "property_enquiry");		return $query->row['total'];	}	public function getAgent($property_agent_id){		$sql="select * from " . DB_PREFIX . "property_agent where property_agent_id='".$property_agent_id."'";		$query=$this->db->query($sql);		return $query->row;	}	}